import React from 'react';

import { userService } from '../Services';

class LoginPage extends React.Component {
    constructor(props) {
        super(props);

        userService.logout();

        this.state = {
            username: '',
            password: '',
            submitted: false,
            loading: false,
            error: ''
        };

        this.handleChange = this.handleChange.bind(this);
        this.handleSubmit = this.handleSubmit.bind(this);
    }

    handleChange(e) {
        const { name, value } = e.target;
        this.setState({ [name]: value });
    }

    handleSubmit(e) {
        e.preventDefault();

        this.setState({ submitted: true });
        const { username, password } = this.state;

        // stop here if form is invalid
        if (!(username && password)) {
            return;
        }

        this.setState({ loading: true });
        userService.login(username, password)
            .then(
                user => {
                    const { from } = this.props.location.state || { from: { pathname: "/" } };
                    this.props.history.push(from);
                },
                error => this.setState({ error, loading: false })
            );
    }

    render() {
        const { username, password, submitted, loading, error } = this.state;
        return (
            <div className="login-page">
                <div className="boxLogin">
                    <h2>Login</h2>
                    <form name="form" onSubmit={this.handleSubmit}>
                        <div className={'form-group-login' + (submitted && !username ? ' has-error' : '')}>
                            <input type="text" className="form-control" name="username" placeholder="Username" value={username} onChange={this.handleChange} />
                            <div className="box-error">
                                {submitted && !username &&
                                    <div className="help-block">Username is required</div>
                                }
                            </div>
                        </div>
                        <div className={'form-group-login' + (submitted && !password ? ' has-error' : '')}>
                            <input type="password" className="form-control" name="password" placeholder="Password" value={password} onChange={this.handleChange} />
                            <div className="box-error">
                                {submitted && !password &&
                                    <div className="help-block">Password is required</div>
                                }
                            </div>
                        </div>
                        <div className="form-group-login">
                            <button className="btn btn-primary" disabled={loading}>Login</button>
                            <div className="login-loading">
                                {loading &&
                                    <img src="data:image/gif;base64," />
                                }
                            </div>
                        </div>
                        <div className="box-error-login">
                            {error &&
                                <div className={'alert-login alert-danger'}>Username or password is incorrect</div>
                            }
                        </div>
                    </form>
                </div>
            </div>
        );
    }
}

export { LoginPage }; 